import { Float } from "../exprtypes";
import { BasicVec2, BasicVec3, BasicVec4, PrimitiveVec2, PrimitiveVec3, PrimitiveVec4 } from "./expr";
/** creates a basic vec2 expression */
export declare function vec2(comp1: Float | number, comp2: Float | number): BasicVec2;
/** creates a basic vec3 expression */
export declare function vec3(comp1: Float | number, comp2: Float | number, comp3: Float | number): BasicVec3;
/** creates a basic vec4 expression */
export declare function vec4(comp1: Float | number, comp2: Float | number, comp3: Float | number, comp4: Float | number): BasicVec4;
/** creates a primitive vec2 expression */
export declare function pvec2(comp1: number, comp2: number): PrimitiveVec2;
/** creates a primitive vec3 expression */
export declare function pvec3(comp1: number, comp2: number, comp3: number): PrimitiveVec3;
/** creates a primitive vec4 expression */
export declare function pvec4(comp1: number, comp2: number, comp3: number, comp4: number): PrimitiveVec4;
